function abstractState(states) {
    this.states = states;
}

abstractState.prototype.__defineGetter__("context", function(){ return this._context; });
abstractState.prototype.__defineSetter__("context", function(value){ this._context = value; });
abstractState.prototype.__defineSetter__("states", function(value){ this._states = value; });
abstractState.prototype.__defineSetter__("state", function(value){
    var c = this.context;
    c.state = new this.states[value](c);
});

module.exports = abstractState;