var $ = require('../../lib/base/asserters'),
    $coord = require('../../lib/geometry/coord'),
    $rectangle = require('../../lib/geometry/rectangle'),
    rectangle = $rectangle($coord.zero(), $coord(6,5));

exports['create'] = function (test) {
    test.expect(1);
    test.ok($rectangle($coord.zero(), $coord(6,5)));
    test.done();
};
exports['topLeft'] = function (test) {
    test.expect(1);
    test.ok(rectangle.topLeft().equals($coord.zero()));
    test.done();
};
exports['bottomRight'] = function (test) {
    test.expect(1);
    test.ok(rectangle.bottomRight().equals($coord(6,5)));
    test.done();
};
exports['bottomRight'] = function (test) {
    test.expect(1);
    test.ok(rectangle.center().equals($coord(3,2.5)));
    test.done();
};
exports['contains'] = function (test) {
    test.expect(7);
    test.ok(rectangle.contains($coord(1,1)));
    test.ok(rectangle.contains($coord(4,4)));
    test.ok(rectangle.contains(rectangle.center()));
    test.ok(!rectangle.contains($coord.zero()));
    test.ok(!rectangle.contains($coord(6,5)));
    test.ok(!rectangle.contains($coord(6,6)));
    test.ok(!rectangle.contains($coord(-1,-1)));
    test.done();
};

