var $kernel = require('ku4node-kernel'),
    $ = $kernel.asserters,
    $queryString = require('../../lib/data/queryString'),
    $str = "sex=m&age=25&married=true&_uri=here%2Fto%2Fthere.go",
    $obj = {sex:"m", age:25, married:true, "_uri":"here/to/there.go"};

exports['serialize'] = function (test) {
    test.expect(1);
    test.equal($queryString.serialize($obj), $str);
    test.done();
};
exports['deserialize'] = function (test) {
    test.expect(4);
    var o = $queryString.deserialize($str);
    test.equal(o.sex, $obj.sex);
    test.equal(o.age, $obj.age);
    test.equal(o.married, $obj.married);
    test.equal(o._uri, $obj._uri);
    test.done();
};