var replicate = require('../../src/base/replicate'),
    array = [1,2,3],
    date = new Date(),
    obj = {"1":1,"2":2,"3":3};

exports['replicate'] = function (test) {
    test.expect(5);
    test.ok(replicate(null) ==  null);
    test.ok(replicate(undefined) == undefined);
    test.ok(array[0] == replicate(array)[0]);
    test.ok(date.valueof == replicate(date).valueof);
    test.ok(obj["1"] == replicate(obj)["1"]);
    test.done();
};
